mapper.select:
select a.*,d.`status` , (select sum(new_num) from ORDER_INFO t where t.user_code = a.user_code and t.order_status in ('1','2')) total_amnt from tb_user_detail a LEFT JOIN tb_user d ON a.user_code = d.user_code where 1=1
and a.user_name like CONCAT(CONCAT('%', #{pd.user_name}),'%')
and a.real_name like CONCAT(CONCAT('%', #{pd.real_name}),'%')
and a.phone like CONCAT(CONCAT('%', #{pd.phone}),'%')
and a.ref_username like CONCAT(CONCAT('%', #{pd.ref_username}),'%')
and d.`status` = #{pd.status}
and a.create_time >= #{pd.startTime}
and substring(a.create_time,1,10)<= #{pd.endTime}
order by a.id desc

select a.user_name, a.real_name, a.phone, a.ref_username ref_username, a.ref_phone, date_format(a.create_time,'%Y-%m-%d %T') create_time1, (case when d.`status`='1' then '有效' else '禁用' end) `status`, (case when a.user_type ='1' then '普通会员' when a.user_type ='2' then 'VIP会员' else '' end) user_type1, (select sum(new_num) from ORDER_INFO t where t.user_code = a.user_code and t.order_status in ('1','2')) total_amnt from tb_user_detail a LEFT JOIN tb_user d ON a.user_code = d.user_code where 1=1
and a.user_name like CONCAT(CONCAT('%', #{pd.user_name}),'%')
and a.real_name like CONCAT(CONCAT('%', #{pd.real_name}),'%')
and a.phone like CONCAT(CONCAT('%', #{pd.phone}),'%')
and a.ref_username like CONCAT(CONCAT('%', #{pd.ref_username}),'%')
and d.`status` = #{pd.status}
and a.create_time >= #{pd.startTime}
and substring(a.create_time,1,10)<= #{pd.endTime}
order by a.id desc

mapper.update:

update tb_user_detail 

       buy_flag = #{buy_flag},
       modify_time = now(),

       operator = #{operator}


where id = #{id}

update tb_user
`status` = #{status} , modify_time = now()
where user_code = #{user_code}

update tb_user
user_type = #{userType} , modify_time = now()
where user_code = #{user_code}

update tb_user_detail
user_type = #{userType} , modify_time = now()
where user_code = #{user_code}

update tb_user_friendship
recomuser_type = #{userType} , modify_time = now()
where recomuser_code = #{user_code}

update tb_user
pwdhash = #{pwdhash} , modify_time = now()
where user_code = #{user_code}

